    arr = arr.slice(longest + 1);
  }

  return res.join("\r");
}

/**
 * Safely escape carrigage returns like a terminal.
 * This allows to escape carrigage returns while allowing future output to be appended
 * without loosing information.
 * Use this as a intermediate escape step if your stream hasn't completed yet.
 * @param {string} txt - String to escape.
 * @return {string}    - Escaped string.
 */
function escapeCarriageReturnSafe(txt) {
  if (!txt) return "";
  if (!/\r/.test(txt)) return txt;
  if (!/\n/.test(txt)) return escapeSingleLineSafe(txt);
  txt = txt.replace(/\r+\n/gm, "\n"); // \r followed by \n --> newline
  var idx = txt.lastIndexOf("\n");

  return (
    escapeCarriageReturn(txt.slice(0, idx)) +
    "\n" +
    escapeSingleLineSafe(txt.slice(idx + 1))
  );
}

module.exports = escapeCarriageReturn;
module.exports.escapeCarriageReturn = escapeCarriageReturn;
module.exports.escapeCarriageReturnSafe = escapeCarriageReturnSafe;
import React, { useState, ReactNode } from 'react';
import Anser, { AnserJsonEntry } from 'anser';
import { escapeCarriageReturn } from 'escape-carriage';
import styles from './log.module.less';
import { Partical } from './Spliter';

const LINK_REGEX = /(https?:\/\/(?:www\.|(?!www))[^\s\.]+\.[^\s]{2,}|www\.[^\s]+\.[^\s]{2,})/;


/**
 * Create a class string.
 * @name createClass
 * @function
 * @param {AnserJsonEntry}.
 * @return {String} class name(s)
 */
function createClass(bundle: AnserJsonEntry) {
  let classNames: string = '';

  if (!bundle.bg && !bundle.fg) {
    return '';
  }
  if (bundle.bg) {
    classNames += bundle.bg + ' ';
  }
  if (bundle.fg) {
    classNames += bundle.fg + ' ';
  }

  classNames = classNames.substring(0, classNames.length - 1);
  return classNames;
}

/**
 * Create the style attribute.
 * @name createStyle
 * @function
 * @param {AnserJsonEntry}.
 * @return {Object} returns the style object
 */
function createStyle(bundle: AnserJsonEntry) {
  const style: { backgroundColor?: string; color?: string } = {};
  if (bundle.bg) {
    style.backgroundColor = `rgb(${bundle.bg})`;
  }
  if (bundle.fg) {
    style.color = `rgb(${bundle.fg})`;
  }

  return style;
}

function ansiToJSON(input: string, useClasses = false) {
  input = escapeCarriageReturn(input);
  return Anser.ansiToJson(input, {
    json: true,
    remove_empty: true,
    use_classes: useClasses,
  });
}

function convertBundleIntoReact(
  useClasses: boolean,
  linkify: boolean,
  bundle: AnserJsonEntry,
  key: number
) {
  const style = useClasses ? {} : createStyle(bundle);
  const className = useClasses ? createClass(bundle) : '';

  let content: ReactNode[] | string = bundle.content;
  if (linkify) {
    content = bundle.content.split(' ').reduce(
      (words, word, index) => {
        if (index === 0) {
          words.push(' ');
        }

        if (!LINK_REGEX.test(word)) {
          words.push(word);
          return words;
        }

        words.push(<a key={index} href={word} target="_blank" rel="noopener noreferer">
          {word}
        </a>);

        return words;
      }, [] as React.ReactNode[]);
    }
  return <span className={className} style={style} key={key}>
    {content}
  </span>;
}

export function RawLogger({
  partical,
  index = 0,
  foldable = false,
  useClasses = false,
  linkify = false,
}: {
  partical: Partical;
  index: number;
  foldable?: boolean;
  useClasses?: boolean;
  linkify?: boolean;
}) {
  const [fold, setFold] = useState(partical.fold);

  const lines = (
    <>
      {partical.content.split('\n').map((line, index) => {
        return (
          <div className={styles.logLine} key={index}>
            <a />
            {ansiToJSON(line).map(convertBundleIntoReact.bind(null, useClasses, linkify))}
          </div>
        );
      })}
    </>
  );

  if (foldable) {
    return (
      <div className={fold ? styles.fold : styles.foldOpen}>
        <div
          key={`folder-placeholder-${index}`}
          className={styles.foldLine}
          onClick={() => setFold(!fold)}
        >
          {partical.label}
        </div>
        {lines}
      </div>
    );
  }

  return lines;
}
/**
 * An foldable ansi logger for react
 * Inspired by ansi-to-react: https://github.com/nteract/nteract/blob/master/packages/ansi-to-react
 */
import React, { useRef } from 'react';
import { _ } from './utils/i18n';
import { defaultSpliters } from './Spliter';

import styles from './log.module.less';
import { RawLogger } from './RawLogger';

export interface FoldableLoggerProps {
  log: string;
  style?: React.CSSProperties;
  bodyStyle?: React.CSSProperties;
}

export default function FoldableLogger({
  style,
  bodyStyle,
  log,
}: FoldableLoggerProps) {
  const bodyRef = useRef<HTMLDivElement>(null);
  const foldedLogger = defaultSpliters.execute(log);

  function scrollBodyToTop() {
    if (!bodyRef.current) {
      return;
    }
    bodyRef.current.scrollTop = 0;
  }

  return (
    <div className={styles.logMain} style={style}>
      <div className={styles.logHeader}>
        <button className={styles.rawLog}>
          {_('rawLog')}
        </button>
      </div>

      <div className={styles.logBody} style={bodyStyle} ref={bodyRef}>
        <pre id="log" className={styles.ansi}>
          {foldedLogger.map((partical, index) => {
            return (
              <RawLogger
                key={`logger-line-${index}`}
                foldable={partical.type === 'partical'}
                partical={partical}
                index={index}
              />
            );
          })}
        </pre>
      </div>
      <div className={styles.logFooter} onClick={scrollBodyToTop}>
        <a className={styles.backToTop}>Top</a>
      </div>
    </div>
  );
}
